std::vector<int> Options::get_default_map()
{
    std::string default_map = default_map2;
    std::vector<int> map;
    std::string buffer;
    for (int i = 0; i < default_map.size(); i++)
    {
        if (default_map[i] == ' ')
        {
            map.push_back(std::stoi(buffer));
            buffer = "";
        }
        else
            buffer += default_map[i];
    }
    return map;
}